$(document).ready(function(e) {

	$("form[ajax=true]").submit(function(e) {

        e.preventDefault();

        var form_data = $(this).serialize();
        var form_url = $(this).attr("action");
        var form_method = $(this).attr("method").toUpperCase();

		var name = $(this).attr("name");

		//console.log($(this).find("input:first").serialize());
		//console.log(form_data);
        $.ajax({
            url: form_url, 
            type: form_method,      
            data: form_data,     
            cache: false,
            success: function(){
				if(name == "DELETE/DEVICE"){
					$.ajax({
						url: '/papelera', 
						type: form_method,     
						data: form_data,     
						cache: false,
						success: function(){
							return;
						}           
					}); 
				}
  				if(name == "LINK/DO"){
					$.ajax({
						url: 'LINK/DO', 
						type: form_method,     
						data: form_data,     
						cache: false,
						success: function(){
							return;
						}           
					}); 
				}
				if(name == "LINK/OA"){
					$.ajax({
						url: 'LINK/OA', 
						type: form_method,     
						data: form_data,     
						cache: false,
						success: function(){
							return;
						}           
					}); 					
				}
				return;
            }           
        });           
    });
});

